const About = ({query1}) => {
  const [name, setName] = useState(query1);

  return (
    <div> 반갑습니다. {name}입니다. ^~^</div>
  );
}

About.getInitialProps = async (context) => {
  console.log('About getInintialProps', context.query.query1);
