import { useEffect } from "react";
import HeaderCompo from "../component/HeaderCompo";

const Layout = ({ Component }) => { //next에서 넣어주는 props, 페이지들을 넣어줌
  return (
    <>
      <HeaderCompo>
        <Component />
      </HeaderCompo>
    </>
  );
}

Layout.getInitalProps = async (context) => { //next에서 내려주는 context, 안에 컴포넌트나 ctx가 들어있음
  await context.Component.getInitalProps(context.ctx);
}
export default Layout;
